mapper.select:
SELECT * FROM ( SELECT (SELECT cascade_id FROM aos_org WHERE id = (SELECT org_id FROM aos_user WHERE id = aos_role.create_by)) AS org_cascade_id,
FROM aos_role
AND name LIKE '%${name}%'
AND type = #{type}
) t WHERE t.org_cascade_id LIKE '${org_cascade_id}%' ORDER BY id DESC

SELECT aos_org.name as org_name, aos_user_role.id as user_role_id,
FROM aos_user LEFT JOIN aos_user_role ON aos_user_role.user_id = aos_user.id LEFT JOIN aos_org ON aos_org.id = aos_user.org_id WHERE aos_user.id IN (SELECT user_id FROM aos_user_role WHERE role_id = #{role_id}) AND aos_user_role.role_id = #{role_id}
AND (aos_user.name LIKE '%${na}%' OR aos_user.account LIKE '%${na}%')
AND aos_user.is_del = #{is_del}
ORDER BY aos_user.id DESC
